Basic:
const Basic = () => {
  const items = ['panel-1', 'panel-2', 'panel-3']
  return (
    <Accordion defaultValue="panel-1">
      {items.map((item, id) => (
        <AccordionItem key={id} value={item}>
          <AccordionTrigger>
            <button>{item} trigger</button>
          </AccordionTrigger>
          <AccordionContent>{item} content</AccordionContent>
        </AccordionItem>
      ))}
    </Accordion>
  )
}

