const Basic = () => {
  return (
    <DatePicker selectionMode="single">
      {(api) => (
        <>
          <DatePickerControl>
            <DatePickerInput />
            <DatePickerTrigger>ğŸ—“</DatePickerTrigger>
            <DatePickerClearTrigger>Clear</DatePickerClearTrigger>
          </DatePickerControl>
          <DatePickerContent>
            <>
              <DatePickerYearSelect />
              <DatePickerMonthSelect />
              <DatePickerGrid>
                <DatePickerRowHeader>
                  {api.weekDays.map((day, i) => (
                    <DatePickerColumnHeader key={i} aria-label={day.long}>
                      {day.narrow}
                    </DatePickerColumnHeader>
                  ))}
                </DatePickerRowHeader>
                <DatePickerRowGroup>
                  {api.weeks.map((week, id) => (
                    <DatePickerRow key={id}>
                      {week.map((day, id) => {
                        if (day === null) return <div />
                        return (
                          <DatePickerDayCell key={id} value={day}>
                            <DatePickerDayCellTrigger>{day.day}</DatePickerDayCellTrigger>
                          </DatePickerDayCell>
                        )
                      })}
                    </DatePickerRow>
                  ))}
                </DatePickerRowGroup>
              </DatePickerGrid>
            </>
          </DatePickerContent>
        </>
      )}
    </DatePicker>
  )
}
