const Basic = () => (
  <RatingGroup max={5} defaultValue={3} allowHalf>
    <RatingGroupLabel>Label</RatingGroupLabel>
    <RatingGroupControl>
      {({ sizeArray }) =>
        sizeArray.map((index) => (
          <Rating key={index} index={index}>
            {({ isHalf, isHighlighted }) => {
              if (isHalf) return <IconHalf />
              if (isHighlighted) return <IconFull />
              return <IconEmpty />
            }}
          </Rating>
        ))
      }
    </RatingGroupControl>
  </RatingGroup>
)
