InitialValue:
const InitialValue = () => (
  <SegmentGroup defaultValue="react">
    <SegmentIndicator />
    {options.map((option, id) => (
      <Segment key={id} value={option.id}>
        <SegmentLabel>{option.label}</SegmentLabel>
        <SegmentInput />
        <SegmentControl />
      </Segment>
    ))}
  </SegmentGroup>
)

Controlled:
const Controlled = () => {
  const [value, setValue] = useState('react')
  return (
    <SegmentGroup value={value} onChange={(e) => setValue(e.value)}>
      <SegmentIndicator />
      {options.map((option, id) => (
        <Segment key={id} value={option.id}>
          <SegmentLabel>{option.label}</SegmentLabel>
          <SegmentInput />
          <SegmentControl />
        </Segment>
      ))}
    </SegmentGroup>
  )
}

Disabled:
const Disabled = () => (
  <SegmentGroup defaultValue="react">
    <SegmentIndicator />
    {options.map((option, id) => (
      <Segment key={id} value={option.id} disabled={option.id === 'svelte'}>
        <SegmentLabel>{option.label}</SegmentLabel>
        <SegmentInput />
        <SegmentControl />
      </Segment>
    ))}
  </SegmentGroup>
)
