const Basic = () => (
  <Splitter
    defaultSize={[
      { id: 'a', size: 50 },
      { id: 'b', size: 50 },
    ]}
  >
    <SplitterPanel id="a">A</SplitterPanel>
    <SplitterResizeTrigger id="a:b" />
    <SplitterPanel id="b">B</SplitterPanel>
  </Splitter>
)

RenderProp:
const RenderProp = () => (
  <Splitter
    defaultSize={[
      { id: 'a', size: 50 },
      { id: 'b', size: 50 },
    ]}
  >
    {(api) => (
      <>
        <SplitterPanel id="a">
          <button onClick={() => api.setSize('a', 10)}>Set to 10%</button>
        </SplitterPanel>
        <SplitterResizeTrigger id="a:b" />
        <SplitterPanel id="b">
          <button onClick={() => api.setSize('b', 10)}>Set to 10%</button>
        </SplitterPanel>
      </>
    )}
  </Splitter>
)

