RenderProp:
const RenderProp = () => {
  const items = ['panel-1', 'panel-2', 'panel-3']
  return (
    <Accordion>
      <For each={items}>
        {(item) => (
          <AccordionItem value={item}>
            {(api) => (
              <>
                <AccordionTrigger>{api().isOpen ? 'Close' : 'Open'}</AccordionTrigger>
                <AccordionContent>{item} content</AccordionContent>
              </>
            )}
          </AccordionItem>
        )}
      </For>
    </Accordion>
  )
}

