const Basic = () => {
  const [options, setOptions] = createSignal(comboboxData)

  const handleInputChange: ComboboxProps['onInputChange'] = ({ value }) => {
    const filtered = comboboxData.filter((item) =>
      item.label.toLowerCase().includes(value.toLowerCase()),
    )
    setOptions(filtered.length > 0 ? filtered : comboboxData)
  }

  return (
    <Combobox onInputChange={handleInputChange}>
      {(context) => (
        <>
          <ComboboxLabel>JS Frameworks</ComboboxLabel>
          <ComboboxControl>
            <ComboboxInput />
            <ComboboxTrigger>
              <button>â–¼</button>
            </ComboboxTrigger>
          </ComboboxControl>
          {context().isInputValueEmpty && !context().isOpen && (
            <div>Give me you favorite framework!</div>
          )}
          <Portal>
            <ComboboxPositioner>
              <ComboboxContent>
                <For each={options()}>
                  {(option) => (
                    <ComboboxOption
                      label={option.label}
                      value={option.value}
                      disabled={option?.disabled}
                    >
                      {option.label}
                    </ComboboxOption>
                  )}
                </For>
              </ComboboxContent>
            </ComboboxPositioner>
          </Portal>
        </>
      )}
    </Combobox>
  )
}
