const Basic = () => (
  <RatingGroup max={5} value={1} allowHalf>
    <RatingGroupLabel>GroupLabel</RatingGroupLabel>
    <RatingGroupControl>
      {(context) =>
        context().sizeArray.map((index) => (
          <Rating index={index}>
            {({ isHalf, isHighlighted }) => {
              if (isHalf) return <IconHalf />
              if (isHighlighted) return <IconFull />
              return <IconEmpty />
            }}
          </Rating>
        ))
      }
    </RatingGroupControl>
  </RatingGroup>
)

