const Disabled = () => {
  const [value, setValue] = createSignal('react')
  return (
    <SegmentGroup value={value()} onChange={({ value }) => setValue(value)}>
      <SegmentIndicator />
      <For each={options}>
        {(option) => (
          <Segment value={option.id} disabled={option.id === 'svelte'}>
            <SegmentLabel>{option.label}</SegmentLabel>
            <SegmentInput />
            <SegmentControl />
          </Segment>
        )}
      </For>
    </SegmentGroup>
  )
}
