ChakraToastProvider:
const ChakraToastProvider = (props: { children: JSX.Element }) => (
  <ToastProvider>
    <ToastPlacements>
      {(placements) => (
        <For each={placements}>
          {(placement) => (
            <ToastGroup placement={placement}>
              {(toasts) => (
                <For each={toasts}>
                  {(toast) => (
                    <Toast toast={toast}>
                      <ToastTitle />
                      <ToastDescription />
                      <ToastCloseTrigger>
                        <button>close</button>
                      </ToastCloseTrigger>
                    </Toast>
                  )}
                </For>
              )}
            </ToastGroup>
          )}
        </For>
      )}
    </ToastPlacements>
    {props.children}
  </ToastProvider>
)
