  <RatingGroup max={5} value={1} allowHalf>
    <RatingGroupLabel>GroupLabel</RatingGroupLabel>
    <RatingGroupControl>
      {(api) => (
        <For each={api().sizeArray}>
          {(index) => (
            <Rating index={index}>
              {(api) => {
                if (api().isHalf) return <IconHalf />
                if (api().isHighlighted) return <IconFull />
                return <IconEmpty />
              }}
            </Rating>
          )}
        </For>
      )}
    </RatingGroupControl>
  </RatingGroup>
)

