import { For, createSignal } from 'solid-js'
import { Rating, RatingGroup, RatingGroupControl, RatingGroupLabel } from '@ark-ui/solid'

const ControlledRatingGroup = () => {
  const [value, setValue] = createSignal(0)

  return (
    <RatingGroup
      max={5}
      value={value()}
      onValueChange={(details) => setValue(details.value)}
      allowHalf
    >
      <RatingGroupLabel>Label</RatingGroupLabel>
      <RatingGroupControl>
        {(api) => (
          <For each={api().sizeArray}>
            {(index) => (
              <Rating index={index}>
                {(api) => {
                  if (api().isHalf) return <IconHalf />
                  if (api().isHighlighted) return <IconFull />
                  return <IconEmpty />
                }}
              </Rating>
            )}
          </For>
        )}
      </RatingGroupControl>
    </RatingGroup>
  )
}
