import { Clipboard } from '@ark-ui/react'

const RenderFn = () => {
  return (
    <Clipboard.Root value="https://ark-ui.com">
      {(api) => (
        <>
          <Clipboard.Label>Copy this link</Clipboard.Label>
          <Clipboard.Control>
            <Clipboard.Input />
            <Clipboard.Trigger>{api.isCopied ? 'Copied' : 'Copy'}</Clipboard.Trigger>
          </Clipboard.Control>
        </>
      )}
    </Clipboard.Root>
  )
}
