Controlled:
import { RatingGroup } from '@ark-ui/react'
import React from 'react'

const Controlled = () => {
  const [value, setValue] = React.useState(0)

  return (
    <RatingGroup.Root
      count={5}
      value={value}
      onChange={(details) => setValue(parseFloat((details.target as HTMLInputElement).value))}
      allowHalf
    >
      <RatingGroup.Label>Label</RatingGroup.Label>
      <RatingGroup.Control>
        {({ items }) =>
          items.map((item) => (
            <RatingGroup.Item key={item} index={item}>
              {({ isHalf, isHighlighted }) => {
                if (isHalf) return <IconHalf />
                if (isHighlighted) return <IconFull />
                return <IconEmpty />
              }}
            </RatingGroup.Item>
          ))
        }
      </RatingGroup.Control>
    </RatingGroup.Root>
  )
}

