import { Switch, SwitchControl, SwitchLabel, SwitchThumb } from '@ark-ui/react'

const RenderProp = () => (
  <Switch>
    {(api) => (
      <>
        <SwitchControl>
          <SwitchThumb />
        </SwitchControl>
        <SwitchLabel>Feature is {api.isChecked ? 'enabled' : 'disabled'}</SwitchLabel>
      </>
    )}
  </Switch>
)
