import { Splitter, SplitterPanel, SplitterResizeTrigger } from '@ark-ui/react'

const Basic = () => (
  <Splitter
    defaultSize={[
      { id: 'a', size: 50 },
      { id: 'b', size: 50 },
    ]}
  >
    <SplitterPanel id="a">A</SplitterPanel>
    <SplitterResizeTrigger id="a:b" />
    <SplitterPanel id="b">B</SplitterPanel>
  </Splitter>
)

