  return (
    <Switch checked={checked()} onChange={(e) => setChecked(e.checked)}>
      <SwitchControl>
        <SwitchThumb />
      </SwitchControl>
      <SwitchLabel>Label</SwitchLabel>
    </Switch>
  )
}
