AppToastProvider:
import { children } from 'solid-js'
import { For } from 'solid-js/web'
import {
  Toast,
  ToastCloseTrigger,
  ToastDescription,
  ToastGroup,
  ToastPlacements,
  ToastProvider,
  ToastTitle,
  useToast,
  type ToastProviderProps,
} from '@ark-ui/solid'

const AppToastProvider = (props: ToastProviderProps) => {
  const getChildren = children(() => props.children)
  return (
    <ToastProvider {...props}>
      <ToastPlacements>
        {(placements) => (
          <For each={placements()}>
            {(placement) => (
              <ToastGroup placement={placement}>
                {(toasts) => (
                  <For each={toasts()}>
                    {(toast) => (
                      <Toast toast={toast}>
                        <ToastTitle />
                        <ToastDescription />
                        <ToastCloseTrigger>Close</ToastCloseTrigger>
                      </Toast>
                    )}
                  </For>
                )}
              </ToastGroup>
            )}
          </For>
        )}
      </ToastPlacements>
      {getChildren()}
    </ToastProvider>
  )
}

