ConfigureToast:
import { children } from 'solid-js'
import { For } from 'solid-js/web'
import {
  Toast,
  ToastCloseTrigger,
  ToastDescription,
  ToastGroup,
  ToastPlacements,
  ToastProvider,
  ToastTitle,
  useToast,
  type ToastProviderProps,
} from '@ark-ui/solid'

const ConfigureToast = () => {
  const toast = useToast()
  return (
    <button
      onClick={() => {
        toast().create({
          title: 'Success',
          description: 'This is a success toast',
          type: 'success',
          placement: 'bottom-start',
          duration: 20000,
          removeDelay: 250,
        })
      }}
    >
      Add Toast
    </button>
  )
}

